import "./Modal.css";

import React, { useState } from 'react';

function App() {
  const [modals, setModals] = useState([false, false, false, false]);

  const toggleModal = (index) => {
    setModals((prevModals) =>
      prevModals.map((modal, i) => (i === index ? !modal : modal))
    );
  };

  return (
    <div>
      {modals.map((modal, index) => (
        <div key={index}>
          <button onClick={() => toggleModal(index)}>
            Open Modal {index + 1}
          </button>
          {modal && (
            <div className={`modal modal-${index + 1}`}>
              <div onClick={() => toggleModal(index)} className="overlay"></div>
              <div className="modal-content">
                <button
                  className="close-modal"
                  onClick={() => toggleModal(index)}
                >
                  CLOSE
                </button>
                <p>This is modal {index + 1}</p>
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default App;
